SELECT c.name, f1.flight_num, f1.origin_city, f1.dest_city, f1.actual_time, f2.flight_num, f2.origin_city, f2.dest_city, f2.actual_time, f1.actual_time+f2.actual_time AS total_flight_time FROM flights f1, flights f2, carriers c, months m WHERE f1.origin_city = 0  AND f2.dest_city = 1 AND f1.dest_city = f2.origin_city AND c.cid = f1.carrier_id AND c.cid = f2.carrier_id AND m.month = 2 AND m.mid = f1.month_id AND m.mid = f2.month_id AND f1.day_of_month = 15 AND f2.day_of_month = 15 AND f1.year = 2015 AND f2.year = 2015 AND f1.actual_time + f2.actual_time < 7*60