select name, a.flight_num, a.origin_city, a.dest_city, a.actual_time, b.flight_num, b.origin_city, b.dest_city, b.actual_time, a.actual_time + b.actual_time as total_time from FLIGHTS as a, FLIGHTS as b, MONTHS, CARRIERS where cid = a.carrier_id AND month = 0 AND a.month_id = mid AND b.month_id = mid AND a.year = 2015 AND b.year = 2015 AND a.day_of_month = 15 AND b.day_of_month = 15 AND a.origin_city = 1 AND b.dest_city = 2 AND a.dest_city = b.origin_city AND a.carrier_id = b.carrier_id AND total_time < 7 * 60
